function kickit(){let e=document.getElementById("background-video");window.scrollTo({left:0,top:0,behavior:"instant"}),e.onplay=function(){let e,t=document.querySelectorAll(".blahblah *");for(e=0;e<t.length;++e)t[e].classList.add("fade-in-test-animation-"+(e+1));document.getElementById("headersss").style.opacity=1,document.getElementById("main-1").style.display="none",document.getElementById("headersss").classList.add("header-animation"),document.getElementById("sec1").classList.add("flash-bg")},e.play()}document.addEventListener("DOMContentLoaded",(function(e){document.getElementById("hider").onclick=kickit;for(var t=document.querySelectorAll(".carousel"),n=0,l=t.length;n<l;n++){var d=t[n];new Flickity(d,{autoPlay:!1,wrapAround:!0,fullscreen:!0})}}));